#To enable NFS share mounting on raspbian, please see: http://www.raspbian.org/RaspbianFAQ#How_do_I_mount_directories_from_other_Linux.2FUnix.2FBSD_servers.3F__How_do_I_mount_an_NFS_share.3F
#
ip_address=<#ip_to_nfs_drive#>;
nfs_path=<#my_local_path_on_nfs_drive#>;
local_mount_path=<#my_local_mount_path#>;

mkdir -p $local_mount_path; sudo mount $ip_address:$nfs_path $local_mount_path;

ip_address=<#ip_to_server#>;
share_name=<#share_name#>;
local_mount_path=<#my_local_mount_path#>;
username=<#myusername#>;
password=<#mypassword#>;

sudo mkdir -p $local_mount_path;
sudo mount -t cifs //$ip_address/$share_name $local_mount_path -o username=$username,password=$password;

searchText=<#mySearchText#>;
filename=<#myfile#>;
sort $filename | uniq -c | sort -nr | grep "$searchText"

user=<#user#>;
file=<#file_path#>;
echo "before:" && ls -l $file;
sudo chown $user $file;echo "after:" && ls -l $file;

user=<#user#>;
file=<#file_path#>;
echo "before:" && ls -l $file;
sudo chgrp $group $file;echo "after:" && ls -l $file;
